
public class BitwiseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;
		int b = 2;
		
		int c = a & b;
		System.out.println(c);
		
		//4 - 0100
		//2 - 0010
		//4&3 - 0
		
		int q = 4;
		System.out.println(q>>2);
		//4 - 0100
		//0010
		//0001
		
		
	}

}
